status.cellBackground:
{{Table1.processedTableData.map((currentRow, currentIndex) => (  (function() {
    if (currentRow.status == 'active') {
        return "#86efac";
    } else if (currentRow.status == 'draft') {
        return "#fef08a";
    } else if (currentRow.status == 'archived') {
				return '#fecaca';
		}
})() ))}}
